#import "RCTDisplayLink.h"

#import <Foundation/Foundation.h>
#if TARGET_OS_IPHONE
#import <QuartzCore/CADisplayLink.h>
#else
#import <CoreVideo/CVDisplayLink.h>
#endif

#import "RCTAssert.h"
#import "RCTBridgeModule.h"
#import "RCTFrameUpdate.h"
#import "RCTModuleData.h"
#import "RCTProfile.h"

#define RCTAssertRunLoop() \
  RCTAssert(_runLoop == [NSRunLoop currentRunLoop], \
  @"This method must be called on the CADisplayLink run loop")

@implementation RCTDisplayLink
{
#if TARGET_OS_IPHONE
  CADisplayLink *_jsDisplayLink;
#else
  // lol. CVDisplayLink later.
  NSTimer *_timer;
  NSTimeInterval _startedAt;
  NSTimeInterval _lastTick;
#endif
  NSMutableSet<RCTModuleData *> *_frameUpdateObservers;
  NSRunLoop *_runLoop;
}

- (instancetype)init
{
  if ((self = [super init])) {
    _frameUpdateObservers = [NSMutableSet new];
#if TARGET_OS_IPHONE
    _jsDisplayLink = [CADisplayLink displayLinkWithTarget:self selector:@selector(_jsThreadUpdate:)];
#else
    [self startTimer];
#endif
  }

  return self;
}

- (void)registerModuleForFrameUpdates:(id<RCTBridgeModule>)module
                       withModuleData:(RCTModuleData *)moduleData
{
  if (![moduleData.moduleClass conformsToProtocol:@protocol(RCTFrameUpdateObserver)] ||
      [_frameUpdateObservers containsObject:moduleData]) {
    return;
  }

  [_frameUpdateObservers addObject:moduleData];

  // Don't access the module instance via moduleData, as this will cause deadlock
  id<RCTFrameUpdateObserver> observer = (id<RCTFrameUpdateObserver>)module;
  __weak typeof(self) weakSelf = self;
  observer.pauseCallback = ^{
    typeof(self) strongSelf = weakSelf;
    if (!strongSelf) {
      return;
    }

    CFRunLoopRef cfRunLoop = [strongSelf->_runLoop getCFRunLoop];
    if (!cfRunLoop) {
      return;
    }

    if ([NSRunLoop currentRunLoop] == strongSelf->_runLoop) {
      [weakSelf updateJSDisplayLinkState];
    } else {
      CFRunLoopPerformBlock(cfRunLoop, kCFRunLoopDefaultMode, ^{
        [weakSelf updateJSDisplayLinkState];
      });
      CFRunLoopWakeUp(cfRunLoop);
    }
  };

  // Assuming we're paused right now, we only need to update the display link's state
  // when the new observer is not paused. If it not paused, the observer will immediately
  // start receiving updates anyway.
  if (![observer isPaused] && _runLoop) {
    CFRunLoopPerformBlock([_runLoop getCFRunLoop], kCFRunLoopDefaultMode, ^{
      [self updateJSDisplayLinkState];
    });
  }
}

- (void)startTimer {
  _timer = [NSTimer scheduledTimerWithTimeInterval:1/60 repeats:YES block:^(NSTimer * _Nonnull timer) {
    [self _jsThreadUpdate];
  }];

  _startedAt = [NSDate date].timeIntervalSince1970;
}

- (void)stopTimer {
  [_timer invalidate];
  _timer = nil;

  _startedAt = 0;
}

- (void)addToRunLoop:(NSRunLoop *)runLoop
{
  _runLoop = runLoop;
#if TARGET_OS_IPHONE
  [_jsDisplayLink addToRunLoop:runLoop forMode:NSRunLoopCommonModes];
#endif
}

- (void)invalidate
{
#if TARGET_OS_IPHONE
  [_jsDisplayLink invalidate];
#endif
}

- (void)dispatchBlock:(dispatch_block_t)block
                queue:(dispatch_queue_t)queue
{
  if (queue == RCTJSThread) {
    block();
  } else if (queue) {
    dispatch_async(queue, block);
  }
}

#if TARGET_OS_IPHONE
- (void)_jsThreadUpdate:(CADisplayLink *)displayLink
#else
- (void)_jsThreadUpdate
#endif
{
#if TARGET_OS_IPHONE
  RCTAssertRunLoop();

  RCT_PROFILE_BEGIN_EVENT(RCTProfileTagAlways, @"-[RCTDisplayLink _jsThreadUpdate:]", nil);
  RCTFrameUpdate *frameUpdate = [[RCTFrameUpdate alloc] initWithDisplayLink:displayLink];
#else
  NSTimeInterval now = [NSDate date].timeIntervalSince1970;
  NSTimeInterval delta = now - _lastTick;
  _lastTick = now;
  RCTFrameUpdate *frameUpdate = [[RCTFrameUpdate alloc] initWithTimestamp:now delta:delta];
  NSLog(@"frame tick");
#endif

  for (RCTModuleData *moduleData in _frameUpdateObservers) {
    id<RCTFrameUpdateObserver> observer = (id<RCTFrameUpdateObserver>)moduleData.instance;
    if (!observer.paused) {
      RCTProfileBeginFlowEvent();

      [self dispatchBlock:^{
        RCTProfileEndFlowEvent();
        [observer didUpdateFrame:frameUpdate];
      } queue:moduleData.methodQueue];
    }
  }

  [self updateJSDisplayLinkState];

#if TARGET_OS_IPHONE
  RCTProfileImmediateEvent(RCTProfileTagAlways, @"JS Thread Tick", displayLink.timestamp, 'g');
  RCT_PROFILE_END_EVENT(RCTProfileTagAlways, @"objc_call");
#endif
}

- (void)updateJSDisplayLinkState
{
#if TARGET_OS_IPHONE
  RCTAssertRunLoop();
#endif

  BOOL pauseDisplayLink = YES;
  for (RCTModuleData *moduleData in _frameUpdateObservers) {
    id<RCTFrameUpdateObserver> observer = (id<RCTFrameUpdateObserver>)moduleData.instance;
    if (!observer.paused) {
      pauseDisplayLink = NO;
      break;
    }
  }

#if TARGET_OS_IPHONE
  _jsDisplayLink.paused = pauseDisplayLink;
#else
  if (pauseDisplayLink) {
    [self stopTimer];
  } else {
    [self startTimer];
  }
#endif
}

@end
